mapper.select:
		SELECT m_Id, M_name, email, address, detail_address, zipcode
  		FROM member_TB 
  		WHERE m_Id = #{m_Id}


			select * 
			from(
			    select rownum rn, o.o_Id, o.m_Id, p.p_Name, o.o_state, o.o_Date
			    from order_TB o
			    JOIN order_item_TB oi
                    on o.o_Id = oi.o_Id
                    JOIN product_TB p
                    on oi.p_id = p.p_Id
			    where rownum <= #{pageNum} * #{amount} 


			and o.m_Id like '%'||#{keyword}||'%'


			    order by o.o_Date desc
			    )
			where rn > (#{pageNum} -1) * #{amount}

select * from( select rownum rn, o.o_Id, p.p_Name, o.o_state, o.o_Date from order_TB o JOIN order_item_TB oi on o.o_Id = oi.o_Id JOIN product_TB p on oi.p_id = p.p_Id where o.m_Id = 'user01' and rownum <= 1 * 10
and p.p_Name like '%'||#{keyword}||'%'
order by o.o_Date desc ) where rn > (#{pageNum} -1) * #{amount}
