mapper.select:

select M_id, M_pwd, M_name, email, contact, zipcode, address, detail_address, joined_date, role
from member_TB 
where M_id = #{m_Id }


select count(*)
from member_TB 
where M_id = #{m_Id }


select M_id
from member_TB 
where email = #{email }


select count(*)
from member_TB 
where email = #{email }


select count(*)
from member_TB 
where email = #{email } AND M_id = #{m_Id }

mapper.update:

update member_TB
set M_pwd = #{M_pwd }, M_name = #{M_name }, contact = #{contact }, zipcode=#{zipcode }, address=#{address }, detail_address=#{detail_address }
where M_id = #{M_id }
