mapper.select:

	select * from (
		select /*+INDEX_DESC(product_TB P_index)*/ rownum as rn, P_id, P_name,
			(select C_Name from category_TB where product_TB.P_category = category_TB.C_Code) 
		C_Name, p_category, P_stock, P_date, p_price
	from product_TB 
	where 


		P_name like '%' || #{keyword} || '%' and


    	rownum <= #{pageNum} * #{amount} 
    )
	where rn > (#{pageNum} - 1) * #{amount}
