mapper.select:

		select * from(  		
			select rownum as rn, r_Id, p_Id, m_Id, r_Content, r_Rating, r_Date
			from review_TB
			where rownum <= #{pageNum} * #{amount}  and p_Id = #{p_Id}	
			order by r_Date desc  	
  			)
  		where rn > (#{pageNum} -1) * #{amount}


select count(*)
from review_TB
where p_Id = #{p_Id}
