mapper.update:
update question_replyy set
	m_Id = #{m_Id},
	rp_content = #{rp_content}
where rno = #{rno}
	and q_Index = #{q_Index}

mapper.delete:
delete from question_replyy
where rno = #{rno}
	and q_index = ${q_Index}

mapper.select:
   select
		rno, q_Index, m_Id, rp_content, rp_Date
	from question_replyy
		where q_Index = #{q_Index}
		and rno = #{rno}
